function applyTheme() {
    const theme = localStorage.getItem('theme');
    if (theme) {
        document.body.className = theme;
    }
}

function toggleTheme() {
    let currentTheme = document.body.className;
    let newTheme = currentTheme === 'dark' ? 'light' : 'dark';
    document.body.className = newTheme;
    localStorage.setItem('theme', newTheme);
}

document.getElementById('back-button').addEventListener('click', () => {
    window.history.back();
});

function goToNextSong() {
    const nextHymn = getAdjacentHymn('next');
    if (nextHymn) {
        window.location.href = `LAEp.html?title=${encodeURIComponent(nextHymn)}`;
    }
}

function goToPreviousSong() {
    const previousHymn = getAdjacentHymn('previous');
    if (previousHymn) {
        window.location.href = `LAEp.html?title=${encodeURIComponent(previousHymn)}`;
    }
}